import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import Logo from "../../components/Logo/Logo";
import styles from "./HomePage.module.css"; // Import CSS Modules

const HomePage = () => {
    const location = useLocation(); // Detectăm ruta curentă

    const [height, setHeight] = useState("");
    const [age, setAge] = useState("");
    const [currentWeight, setCurrentWeight] = useState("");
    const [desiredWeight, setDesiredWeight] = useState("");
   

const [placeholders, setPlaceholders] = useState({
    height: "Height (cm) *",
    age: "Age *",
    currentWeight: "Current weight *",
    desiredWeight: "Desired weight *"
});

const handleFocus = (field) => {
    setPlaceholders(prev => ({ ...prev, [field]: "" }));
};

const handleBlur = (field, value, defaultText) => {
    if (!value) {
        setPlaceholders(prev => ({ ...prev, [field]: defaultText }));
    }
};

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-white p-6">
            {/* ✅ Header */}
            <header className={styles.header}>
                <div className={styles.logo}>
                    <Logo />
                </div>
                <div className={styles.verticalBar}></div>
                <nav className={styles.nav}>
                    <Link to="/login" className={styles.navLink}>Login</Link>
                    <Link to="/register" className={styles.navLink}>Register</Link>
                </nav>
            </header>

            {/* ✅ Ascundem formularul când suntem pe Login sau Register */}
            {!(location.pathname === "/login" || location.pathname === "/register") && (
                <>
                    {/* ✅ Titlu */}
                    <h1 className={styles.title}>
                        Calculate your daily calorie intake right now
                    </h1>

                    {/* ✅ Formular */}
                    <form className={styles.formContainer}>
                        {/* ✅ Coloana Stângă */}
                        <div className={styles.leftColumn}>
                        <input 
    type="number" 
    placeholder={placeholders.height} 
    value={height} 
    onChange={(e) => setHeight(e.target.value)} 
    onFocus={() => handleFocus("height")}
    onBlur={() => handleBlur("height", height, "Height (cm) *")}
    className={styles.inputField} 
/>

<input 
    type="number" 
    placeholder={placeholders.age} 
    value={age} 
    onChange={(e) => setAge(e.target.value)} 
    onFocus={() => handleFocus("age")}
    onBlur={() => handleBlur("age", age, "Age *")}
    className={styles.inputField} 
/>

<input 
    type="number" 
    placeholder={placeholders.currentWeight} 
    value={currentWeight} 
    onChange={(e) => setCurrentWeight(e.target.value)} 
    onFocus={() => handleFocus("currentWeight")}
    onBlur={() => handleBlur("currentWeight", currentWeight, "Current weight *")}
    className={styles.inputField} 
/>
                        </div>

                        {/* ✅ Coloana Dreaptă */}
                        <div className={styles.rightColumn}>
                        <input 
    type="number" 
    placeholder={placeholders.desiredWeight} 
    value={desiredWeight} 
    onChange={(e) => setDesiredWeight(e.target.value)} 
    onFocus={() => handleFocus("desiredWeight")}
    onBlur={() => handleBlur("desiredWeight", desiredWeight, "Desired weight *")}
    className={styles.inputField} 
/>

                            {/* ✅ Blood Type */}
                            <div className={styles.bloodTypeContainer}>
                                <label className={styles.bloodTypeLabel}>Blood Type*</label>
                                <ul className={styles.bloodTypeOptions}>
                                    {[1, 2, 3, 4].map((type) => (
                                        <li key={type}>
                                            <input type="radio" name="bloodType" value={type} checked={bloodType === String(type)} onChange={(e) => setBloodType(e.target.value)} />
                                            {type}
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        </div>

                        {/* ✅ Buton */}
                        <button type="submit" className={styles.submitButton}>Start losing weight</button>
                    </form>
                </>
            )}
        </div>
    );
};

export default HomePage;



 const [bloodType, setBloodType] = useState("");
