import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    dailyCaloriesByDate: {}, // âœ… StocÄƒm caloriile zilnice per zi { "YYYY-MM-DD": 1800 }
    consumedFoods: {}, // âœ… Alimente consumate per zi { "YYYY-MM-DD": [{ food item }] }
};

const caloriesSlice = createSlice({
    name: "calories",
    initialState,
    reducers: {
        // âœ… SetÄƒm caloriile zilnice pentru o anumitÄƒ zi
        setDailyCalories: (state, action) => {
            const { date, calories } = action.payload;
            const formattedDate = new Date(date).toISOString().split("T")[0]; // ðŸ”¥ Format YYYY-MM-DD

            state.dailyCaloriesByDate[formattedDate] = calories;
        },

        // âœ… AdÄƒugÄƒm un produs consumat pe baza datei selectate
        addConsumedFood: (state, action) => {
            const { date, name, weight, calories } = action.payload;
            const formattedDate = new Date(date).toISOString().split("T")[0];

            if (!state.consumedFoods[formattedDate]) {
                state.consumedFoods[formattedDate] = [];
            }

            state.consumedFoods[formattedDate].push({ name, weight, calories });
        },

        // âœ… EliminÄƒm un produs consumat pentru ziua selectatÄƒ
        removeConsumedFood: (state, action) => {
            const { date, index } = action.payload;
            const formattedDate = new Date(date).toISOString().split("T")[0];

            if (state.consumedFoods[formattedDate]) {
                const updatedFoods = [...state.consumedFoods[formattedDate]];
                updatedFoods.splice(index, 1);

                if (updatedFoods.length > 0) {
                    state.consumedFoods[formattedDate] = updatedFoods;
                } else {
                    delete state.consumedFoods[formattedDate]; // EliminÄƒm ziua dacÄƒ e goalÄƒ
                }
            }
        },
    },
});

export const { setDailyCalories, addConsumedFood, removeConsumedFood } = caloriesSlice.actions;
export default caloriesSlice.reducer;
