import { configureStore } from "@reduxjs/toolkit";
import userReducer from "./userSlice";
import caloriesReducer from "./caloriesSlice";  // ✅ Importăm reducer-ul

const store = configureStore({
    reducer: {
        user: userReducer,
        calories: caloriesReducer  // ✅ Adăugăm reducer-ul în Store
    }
});

export default store;
