import React from "react";
import { useSelector } from "react-redux";
import styles from "./Summary.module.css";

const Summary = () => {
    const caloriesData = useSelector((state) => state.calories); // ✅ Verificăm dacă există date

    if (!caloriesData) {
        return <p>Loading...</p>;  // ✅ Prevenim eroarea
    }

    const { dailyCalories, consumedCalories, leftCalories, percentage } = caloriesData;

    return (
        <div className={styles.summaryContainer}>
            <h2>Summary for Today</h2>
            <p>Left: {leftCalories} kcal</p>
            <p>Consumed: {consumedCalories} kcal</p>
            <p>Daily Rate: {dailyCalories} kcal</p>
            <p>{percentage}% of normal</p>
        </div>
    );
};

export default Summary;
