import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    dailyCalories: 0,
    consumedCalories: 0,
    forbiddenFoods: [],
};

const caloriesSlice = createSlice({
    name: "calories",
    initialState,
    reducers: {
        setDailyCalories: (state, action) => {
            state.dailyCalories = action.payload;
        },
        addConsumedCalories: (state, action) => {
            state.consumedCalories += action.payload;
        },
        setForbiddenFoods: (state, action) => {
            state.forbiddenFoods = action.payload;
        },
    },
});

export const { setDailyCalories, addConsumedCalories, setForbiddenFoods } = caloriesSlice.actions;
export default caloriesSlice.reducer;
