import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    dailyCaloriesByDate: {}, // ðŸ”¥ StocÄƒm caloriile zilnice per zi { "YYYY-MM-DD": 1800 }
    consumedFoods: {}, // ðŸ”¥ StocÄƒm mÃ¢ncarea per zi { "YYYY-MM-DD": [{ food item }] }
};

const caloriesSlice = createSlice({
    name: "calories",
    initialState,
    reducers: {
        setDailyCalories: (state, action) => {
            const { date, calories } = action.payload;
            const formattedDate = new Date(date).toISOString().split("T")[0];

            if (!state.dailyCaloriesByDate) {
                state.dailyCaloriesByDate = {};
            }

            state.dailyCaloriesByDate[formattedDate] = calories;
        },

        addConsumedFood: (state, action) => {
            const { date, name, weight, calories } = action.payload;
            const formattedDate = new Date(date).toISOString().split("T")[0];

            if (!state.consumedFoods[formattedDate]) {
                state.consumedFoods[formattedDate] = [];
            }

            state.consumedFoods[formattedDate].push({ name, weight, calories });
        },
    },
});

export const { setDailyCalories, addConsumedFood } = caloriesSlice.actions;
export default caloriesSlice.reducer;
