import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { setSelectedDate } from "../../redux/caloriesSlice";
import DiaryDateCalendar from "../../components/DiaryDateCalendar/DiaryDateCalendar";
import DiaryAddProductForm from "../../components/DiaryAddProductForm/DiaryAddProductForm";
import DiaryProductsList from "../../components/DiaryProductsList/DiaryProductsList";
import Summary from "../../components/Summary/Summary";
import styles from "./DiaryPage.module.css";

const DiaryPage = () => {
    const dispatch = useDispatch();
    const selectedDate = useSelector((state) => state.calories.selectedDate);

    // âœ… Ensure `selectedDate` is a valid Date object
    let dateObject;
    try {
        dateObject = new Date(selectedDate);
        if (isNaN(dateObject.getTime())) {
            throw new Error("Invalid date, setting to today");
        }
    } catch (error) {
        console.error("ðŸš¨ Invalid selectedDate in Redux. Resetting to today:", error);
        dateObject = new Date();
        dispatch(setSelectedDate(dateObject.toISOString().split("T")[0])); // Fix Redux state
    }

    const formattedDate = dateObject.toISOString().split("T")[0];

    const dailyCaloriesByDate = useSelector((state) => state.calories.dailyCaloriesByDate || {});
    const consumedFoods = useSelector((state) => state.calories.consumedFoods || {});

    console.log("ðŸ“Œ [DiaryPage] Selected Date:", formattedDate);
    console.log("ðŸ“Œ [DiaryPage] dailyCaloriesByDate:", dailyCaloriesByDate);
    console.log("ðŸ“Œ [DiaryPage] consumedFoods:", consumedFoods);

    const dailyCalories = dailyCaloriesByDate[formattedDate] || 0;

    // âœ… Function to update Redux state when the date changes
    const handleDateChange = (newDate) => {
        dispatch(setSelectedDate(newDate.toISOString().split("T")[0]));
    };

    return (
        <div className={styles.diaryContainer}>
            <div className={styles.leftContent}>
                {/* âœ… Use Redux date and update it in Redux on change */}
                <DiaryDateCalendar selectedDate={dateObject} setSelectedDate={handleDateChange} />
                <DiaryAddProductForm selectedDate={dateObject} />
                <DiaryProductsList selectedDate={dateObject} />
            </div>

            <div className={styles.rightContent}>
                <h3 className={styles.dailyCaloriesTitle}>Daily Calories Goal: {dailyCalories.toFixed(2)} kcal</h3>
                
                {/* âœ… Pass `selectedDate` correctly to Summary */}
                <Summary selectedDate={dateObject} />
            </div>
        </div>
    );
};

export default DiaryPage;
