import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    dailyCalories: 0,
    consumedFoods: {}, // ✅ Stores foods by date { "YYYY-MM-DD": [ {name, weight, calories}, ...] }
};

const caloriesSlice = createSlice({
    name: "calories",
    initialState,
    reducers: {
        // ✅ Set daily calorie intake
        setDailyCalories: (state, action) => {
            state.dailyCalories = action.payload;
        },

        // ✅ Add a consumed food to the selected date
        addConsumedFood: (state, action) => {
            const { date, name, weight, calories } = action.payload;
            const formattedDate = new Date(date).toISOString().split("T")[0]; // Ensure correct date format

            if (!state.consumedFoods[formattedDate]) {
                state.consumedFoods[formattedDate] = [];
            }

            state.consumedFoods[formattedDate].push({ name, weight, calories });
        },

        // ✅ Remove a food from the selected date
        removeConsumedFood: (state, action) => {
            const { date, index } = action.payload;
            const formattedDate = new Date(date).toISOString().split("T")[0];

            if (state.consumedFoods[formattedDate]) {
                state.consumedFoods[formattedDate].splice(index, 1);

                // ✅ If no foods remain for that date, delete the entry
                if (state.consumedFoods[formattedDate].length === 0) {
                    delete state.consumedFoods[formattedDate];
                }
            }
        },
    },
});

// ✅ Export actions
export const { setDailyCalories, addConsumedFood, removeConsumedFood } = caloriesSlice.actions;

// ✅ Export reducer
export default caloriesSlice.reducer;
