import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    dailyCalories: 0,  // Caloriile recomandate zilnic
    consumedCalories: 0,  // Caloriile consumate de utilizator
};

const caloriesSlice = createSlice({
    name: "calories",
    initialState,
    reducers: {
        setDailyCalories: (state, action) => {
            state.dailyCalories = action.payload;
        },
        addConsumedCalories: (state, action) => {
            state.consumedCalories += action.payload; // ✅ Adăugăm/scădem caloriile consumate
        },
    }
});

export const { setDailyCalories, addConsumedCalories } = caloriesSlice.actions;
export default caloriesSlice.reducer;
