import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { removeConsumedFood } from "../../redux/caloriesSlice";
import styles from "./DiaryProductsList.module.css";

const DiaryProductsList = ({ selectedDate }) => {
    const dispatch = useDispatch();
    
    // ✅ Ensure `selectedDate` is always a Date object
    let dateObject;
    try {
        dateObject = selectedDate instanceof Date ? selectedDate : new Date(selectedDate);
        if (isNaN(dateObject)) throw new Error("Invalid date");
    } catch (error) {
        dateObject = new Date();
    }

    const formattedDate = dateObject.toISOString().split("T")[0]; // ✅ Safe conversion

    // ✅ Use `JSON.parse(JSON.stringify())` to avoid revoked proxy errors
    const consumedFoods = JSON.parse(JSON.stringify(useSelector((state) => state.calories.consumedFoods[formattedDate] || [])));

    return (
        <div className={styles.productsListContainer}>
            {consumedFoods.length === 0 ? (
                <p>No products added yet.</p>
            ) : (
                <ul className={styles.productsList}>
                    {consumedFoods.map((food, index) => (
                        <li key={index}>
                            <span className={styles.productName}>{food.name}</span>
                            <span className={styles.productWeight}>{food.weight}g</span>
                            <span className={styles.productCalories}>{food.calories.toFixed(2)} kcal</span>
                            <button 
                                className={styles.deleteButton} 
                                onClick={() => dispatch(removeConsumedFood({ date: formattedDate, index }))}
                            >
                                ✕
                            </button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default DiaryProductsList;
