import React from "react";
import { useSelector } from "react-redux";
import styles from "./Summary.module.css";

const getFormattedDate = (selectedDate) => {
    if (!selectedDate) return "N/A"; // ✅ Prevents crash if undefined
    const dateObj = new Date(selectedDate); // Ensure it's a valid Date object
    return `${String(dateObj.getDate()).padStart(2, "0")}.${String(dateObj.getMonth() + 1).padStart(2, "0")}.${dateObj.getFullYear()}`;
};

const Summary = ({ selectedDate = new Date() }) => { // ✅ Default value for selectedDate
    const formattedDate = new Date(selectedDate).toISOString().split("T")[0];

    const { dailyCalories = 0, consumedFoods = {} } = useSelector((state) => state.calories || {});

    // ✅ Get consumed foods for selected date
    const dailyConsumedFoods = consumedFoods[formattedDate] || [];
    const consumedCalories = dailyConsumedFoods.reduce((total, food) => total + food.calories, 0);

    // ✅ Calculate Left Calories
    const remainingCalories = dailyCalories - consumedCalories;

    // ✅ Calculate Percentage
    const percentageOfDailyIntake = dailyCalories > 0 ? ((consumedCalories / dailyCalories) * 100).toFixed(1) : 0;

    return (
        <div className={styles.summaryContainer}>
            <h3 className={styles.summaryTitle}>Summary for {getFormattedDate(selectedDate)}</h3>
            <ul className={styles.summaryList}>
                <li><span>Left:</span> {remainingCalories} kcal</li>
                <li><span>Consumed:</span> {consumedCalories} kcal</li>
                <li><span>Daily Rate:</span> {dailyCalories} kcal</li>
                <li><span>% of normal:</span> {percentageOfDailyIntake}%</li>
            </ul>
        </div>
    );
};

export default Summary;
