import React from "react";
import { useSelector } from "react-redux";
import styles from "./Summary.module.css";

const Summary = () => {
    const { dailyCalories, consumedCalories, forbiddenFoods } = useSelector((state) => state.calories);

    const leftCalories = dailyCalories - consumedCalories;
    const percentage = ((consumedCalories / dailyCalories) * 100).toFixed(1);

    return (
        <aside className={styles.sidebar}>
            <div className={styles.summaryContainer}>
                <h3 className={styles.summaryTitle}>Summary for today</h3>
                
                <div className={styles.calorieInfo}>
                    <p>Left: <span>{leftCalories > 0 ? leftCalories : 0} kcal</span></p>
                    <p>Consumed: <span>{consumedCalories} kcal</span></p>
                    <p>Daily rate: <span>{dailyCalories} kcal</span></p>
                    <p>{percentage}% of normal</p>
                </div>

                <hr className={styles.divider} />

                <h4 className={styles.foodsTitle}>Foods not recommended</h4>
                <ul className={styles.forbiddenList}>
                    {forbiddenFoods.map((food, index) => (
                        <li key={index}>{food.title}</li>
                    ))}
                </ul>

                <button className={styles.logoutButton}>Exit</button>
            </div>
        </aside>
    );
};

export default Summary;
