import React, { useState } from "react";
import Logo from "../../components/Logo/Logo";

const HomePage = () => {
    const [height, setHeight] = useState("");
    const [age, setAge] = useState("");
    const [currentWeight, setCurrentWeight] = useState("");
    const [desiredWeight, setDesiredWeight] = useState("");
    const [bloodType, setBloodType] = useState("");

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-white p-6">
            {/* ✅ Logo & Navbar */}
            <header className="w-full flex justify-between items-center px-4">
                <Logo />
                <nav className="flex gap-4">
                    <a href="/login" className="text-blue-500 hover:underline">Login</a>
                    <a href="/register" className="text-blue-500 hover:underline">Register</a>
                </nav>
            </header>

            {/* ✅ Titlu */}
            <h1 className="text-2xl font-bold text-gray-800 text-center mt-6">
                Calculate your daily calorie intake right now
            </h1>

            {/* ✅ Formular */}
            <form className="w-full max-w-md bg-gray-100 p-6 rounded-lg shadow-lg mt-6">
                <div className="grid grid-cols-1 gap-4">
                    <input 
                        type="number" 
                        placeholder="Height (cm) *" 
                        value={height} 
                        onChange={(e) => setHeight(e.target.value)} 
                        className="p-2 border-b-2 border-gray-400 outline-none focus:border-blue-500 transition"
                    />
                    <input 
                        type="number" 
                        placeholder="Age *" 
                        value={age} 
                        onChange={(e) => setAge(e.target.value)} 
                        className="p-2 border-b-2 border-gray-400 outline-none focus:border-blue-500 transition"
                    />
                    <input 
                        type="number" 
                        placeholder="Current weight *" 
                        value={currentWeight} 
                        onChange={(e) => setCurrentWeight(e.target.value)} 
                        className="p-2 border-b-2 border-gray-400 outline-none focus:border-blue-500 transition"
                    />
                    <input 
                        type="number" 
                        placeholder="Desired weight *" 
                        value={desiredWeight} 
                        onChange={(e) => setDesiredWeight(e.target.value)} 
                        className="p-2 border-b-2 border-gray-400 outline-none focus:border-blue-500 transition"
                    />

                    {/* ✅ Blood Type Selection */}
                    <div>
                        <label className="block mb-2 text-gray-700">Blood Type *</label>
                        <ul className="flex justify-around">
                            {[1, 2, 3, 4].map((type) => (
                                <li key={type} className="flex items-center">
                                    <input 
                                        type="radio" 
                                        name="bloodType" 
                                        value={type} 
                                        checked={bloodType === String(type)} 
                                        onChange={(e) => setBloodType(e.target.value)}
                                        className="mr-2"
                                    />
                                    {type}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>

                {/* ✅ Butonul de Submit */}
                <button 
                    type="submit" 
                    className="w-full mt-6 bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600 transition"
                >
                    Start Losing Weight
                </button>
            </form>
        </div>
    );
};

export default HomePage;
