import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    consumedFoods: [],  // ✅ Stores the added foods
    dailyCalories: 0,   // ✅ Daily calorie limit
    consumedCalories: 0 // ✅ Calories consumed so far
};

const caloriesSlice = createSlice({
    name: "calories",
    initialState,
    reducers: {
        addConsumedFood: (state, action) => {
            state.consumedFoods.push(action.payload);
            state.consumedCalories += action.payload.calories; // ✅ Update total calories
        },
        removeConsumedFood: (state, action) => {
            const removedFood = state.consumedFoods.splice(action.payload, 1)[0];
            state.consumedCalories -= removedFood.calories; // ✅ Deduct from total
        }
    }
});

export const { addConsumedFood, removeConsumedFood } = caloriesSlice.actions;
export default caloriesSlice.reducer;
