import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import Logo from "../../components/Logo/Logo";
import styles from "./HomePage.module.css"; 

const HomePage = () => {
    const location = useLocation();

    const [height, setHeight] = useState("");
    const [age, setAge] = useState("");
    const [currentWeight, setCurrentWeight] = useState("");
    const [desiredWeight, setDesiredWeight] = useState("");
    const [bloodType, setBloodType] = useState("");

    useEffect(() => {
        // Resetăm placeholder-urile la fiecare refresh
        document.querySelectorAll("input").forEach((input) => {
            if (!input.value) {
                input.placeholder = input.dataset.placeholder || input.placeholder;
            }
        });
    }, []);

    const handleFocus = (e) => {
        e.target.dataset.placeholder = e.target.placeholder; // Salvăm placeholder-ul inițial
        e.target.placeholder = ""; // Eliminăm placeholder-ul
    };

    const handleBlur = (e) => {
        if (!e.target.value) {
            e.target.placeholder = e.target.dataset.placeholder; // Restaurăm placeholder-ul dacă input-ul este gol
        }
    };

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-white p-6">
        
            <header className={styles.header}>
                <div className={styles.logo}>
                    <Logo />
                </div>
                <div className={styles.verticalBar}></div>
                <nav className={styles.nav}>
                    <Link to="/login" className={styles.navLink}>Log in</Link>
                    <Link to="/register" className={styles.navLink}>Registration</Link>
                </nav>
            </header>

            {!(location.pathname === "/login" || location.pathname === "/register") && (
                <>
                    <h1 className={styles.title}>
                        Calculate your daily calorie intake right now
                    </h1>

                    <form className={styles.formContainer}>
                        <div className={styles.leftColumn}>
                            <input 
                                type="number" 
                                placeholder="Height (cm) *"
                                value={height} 
                                onChange={(e) => setHeight(e.target.value)}
                                onFocus={handleFocus}
                                onBlur={handleBlur}
                                className={styles.inputField} 
                            />

                            <input 
                                type="number" 
                                placeholder="Age *" 
                                value={age} 
                                onChange={(e) => setAge(e.target.value)}
                                onFocus={handleFocus}
                                onBlur={handleBlur}
                                className={styles.inputField} 
                            />

                            <input 
                                type="number" 
                                placeholder="Current weight *" 
                                value={currentWeight} 
                                onChange={(e) => setCurrentWeight(e.target.value)}
                                onFocus={handleFocus}
                                onBlur={handleBlur}
                                className={styles.inputField} 
                            />
                        </div>

                        <div className={styles.rightColumn}>
                            <input 
                                type="number" 
                                placeholder="Desired weight *" 
                                value={desiredWeight} 
                                onChange={(e) => setDesiredWeight(e.target.value)}
                                onFocus={handleFocus}
                                onBlur={handleBlur}
                                className={styles.inputField} 
                            />

                            <div className={styles.bloodTypeContainer}>
                                <label className={styles.bloodTypeLabel}>Blood Type*</label>
                                <ul className={styles.bloodTypeOptions}>
                                    {[1, 2, 3, 4].map((type) => (
                                        <li key={type}>
                                            <input type="radio" name="bloodType" value={type} checked={bloodType === String(type)} onChange={(e) => setBloodType(e.target.value)} />
                                            {type}
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        </div>

                        <button type="submit" className={styles.submitButton}>Start losing weight</button>
                    </form>
                </>
            )}
        </div>
    );
};

export default HomePage;
