import React, { useState } from "react";
import styles from './CalculatorPage.module.css';

const CalculatorPage = () => {
    
    const [height, setHeight] = useState("");
    const [age, setAge] = useState("");
    const [desiredWeight, setDesiredWeight] = useState("");
    const [calories, setCalories] = useState(null);
    const [error, setError] = useState(""); // ✅ 2. Dacă nu-l folosești, șterge-l sau afișează-l în pagină
const [bloodType, setBloodType] = useState("");
 const [currentWeight, setCurrentWeight] = useState("");

    const calculateCalories = async (e) => {
        e.preventDefault();
        setError("");

        if (!weight || !height || !age || !desiredWeight) {
            setError("Please fill in all fields!");
            return;
        }

        try {
            const response = await fetch("http://localhost:5000/api/calories", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ weight, height, age, desiredWeight }),
            });

            const data = await response.json();
            if (!response.ok) {
                throw new Error(data.message || "Calculation failed");
            }

            setCalories(data.dailyCalories);
        } catch (error) {
            setError(error.message);
        }
    };

    return (
        <div className="p-4">
            <h1 className={styles.title}>Calculate your daily calorie intake right now</h1>

            <form className={styles.formContainer} onSubmit={calculateCalories}>
                        <div className={styles.leftColumn}>
                            <input 
                                type="number" 
                                placeholder="Height (cm) *"
                                value={height} 
                                onChange={(e) => setHeight(e.target.value)}
                                className={styles.inputField} 
                            />

                            <input 
                                type="number" 
                                placeholder="Age *" 
                                value={age} 
                                onChange={(e) => setAge(e.target.value)}
                                className={styles.inputField} 
                            />

                            <input 
                                type="number" 
                                placeholder="Current weight *" 
                                value={currentWeight} 
                                onChange={(e) => setCurrentWeight(e.target.value)}
                                className={styles.inputField} 
                            />
                        </div>

                        <div className={styles.rightColumn}>
                            <input 
                                type="number" 
                                placeholder="Desired weight *" 
                                value={desiredWeight} 
                                onChange={(e) => setDesiredWeight(e.target.value)}
                                className={styles.inputField} 
                            />

                            {/* ✅ Blood Type */}
                            <div className={styles.bloodTypeContainer}>
                                <label className={styles.bloodTypeLabel}>Blood Type*</label>
                                <ul className={styles.bloodTypeOptions}>
                                    {[1, 2, 3, 4].map((type) => (
                                        <li key={type}>
                                            <input 
                                                type="radio" 
                                                name="bloodType" 
                                                value={type} 
                                                checked={bloodType === String(type)} 
                                                onChange={(e) => setBloodType(e.target.value)} 
                                            />
                                            <span>{type}</span>
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        </div>

                        <button type="submit" className={styles.submitButton}>Start losing weight</button>
                    </form>

            {calories && (
                <div className="result">
                    <h3>Calorii zilnice necesare: {calories}</h3>
                </div>
            )}
        </div>
    );
};

export default CalculatorPage;
