import React from "react";
import styles from "./DiaryDateCalendar.module.css";

const DiaryDateCalendar = ({ selectedDate, setSelectedDate }) => {
    return (
        <div className={styles.calendarContainer}>
            <input
                type="date"
                value={selectedDate.toISOString().split("T")[0]} // ✅ Convertim data în string format corect
                onChange={(e) => setSelectedDate(e.target.value)} // ✅ Pasăm valoarea corect
                className={styles.datePicker}
            />
        </div>
    );
};

export default DiaryDateCalendar;
