import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addConsumedFood, removeConsumedFood } from "../../redux/caloriesSlice";
import DiaryDateCalendar from "../../components/DiaryDateCalendar/DiaryDateCalendar";
import DiaryAddProductForm from "../../components/DiaryAddProductForm/DiaryAddProductForm";
import DiaryProductsList from "../../components/DiaryProductsList/DiaryProductsList";
import Summary from "../../components/Summary/Summary";
import styles from "./DiaryPage.module.css";

const DiaryPage = () => {
    const dispatch = useDispatch();
    const [selectedDate, setSelectedDate] = useState(new Date());

    // ✅ Format date to match the Redux store
    const formattedDate = `${String(selectedDate.getDate()).padStart(2, "0")}.${String(selectedDate.getMonth() + 1).padStart(2, "0")}.${selectedDate.getFullYear()}`;

    // ✅ Get consumed foods for the selected date
    const consumedFoods = useSelector((state) => state.calories.consumedFoods[formattedDate]) || [];

    const handleAddFood = (food) => {
        dispatch(addConsumedFood(food));
    };

    const handleDeleteFood = (index) => {
        dispatch(removeConsumedFood({ date: formattedDate, index }));
    };

    return (
        <div className={styles.diaryContainer}>
            <DiaryDateCalendar selectedDate={selectedDate} setSelectedDate={setSelectedDate} />
            <DiaryAddProductForm selectedDate={formattedDate} onAddFood={handleAddFood} />
            <DiaryProductsList consumedFoods={consumedFoods} onDeleteFood={handleDeleteFood} />
            <Summary />
        </div>
    );
};

export default DiaryPage;
