import React, { useState } from "react";

const HomePage = () => {
    const [weight, setWeight] = useState("");
    const [height, setHeight] = useState("");
    const [age, setAge] = useState("");
    const [desiredWeight, setDesiredWeight] = useState("");
    const [calories, setCalories] = useState(null);
    const [products, setProducts] = useState([]);

    const calculateCalories = async () => {
        const response = await fetch("http://localhost:5000/api/calories", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ weight, height, age, desiredWeight }),
        });

        const data = await response.json();
        setCalories(data.dailyCalories);
        fetchProducts();
    };

    const fetchProducts = async () => {
        const response = await fetch("http://localhost:5000/api/products");
        const data = await response.json();
        setProducts(data);
    };

    return (
        <div className="p-4">
            <h2>Calculează Caloriile Zilnice</h2>
            <form
                onSubmit={(e) => {
                    e.preventDefault();
                    calculateCalories();
                }}
            >
                <input type="number" placeholder="Greutate (kg)" value={weight} onChange={(e) => setWeight(e.target.value)} required />
                <input type="number" placeholder="Înălțime (cm)" value={height} onChange={(e) => setHeight(e.target.value)} required />
                <input type="number" placeholder="Vârsta" value={age} onChange={(e) => setAge(e.target.value)} required />
                <input type="number" placeholder="Greutate dorită (kg)" value={desiredWeight} onChange={(e) => setDesiredWeight(e.target.value)} required />
                <button type="submit">Start Losing Weight</button>
            </form>

            {calories && (
                <div className="modal">
                    <h3>Calorii zilnice necesare: {calories}</h3>
                    <h4>Produse interzise:</h4>
                    <ul>
                        {products.map((product) => (
                            <li key={product.id}>{product.name}</li>
                        ))}
                    </ul>
                    <button onClick={() => window.location.href = "/register"}>Start Losing Weight</button>
                </div>
            )}
        </div>
    );
};

export default HomePage;
