import React from "react";
import styles from "./DiaryDateCalendar.module.css";

const DiaryDateCalendar = ({ selectedDate, setSelectedDate }) => {
    return (
        <div className={styles.calendarContainer}>
            {/* ✅ Wrapper pentru input și iconiță */}
            <div className={styles.datePickerWrapper}>
                {/* ✅ Input ascuns */}
                <input
                    type="date"
                    value={selectedDate.toISOString().split("T")[0]}
                    onChange={(e) => setSelectedDate(new Date(e.target.value))}
                    className={styles.hiddenDateInput} // Ascundem input-ul default
                />

                {/* ✅ SVG personalizat */}
                <svg className={styles.calendarIcon} viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path
                        d="M7 10H11V14H7V10ZM13 10H17V14H13V10ZM8 2V4H16V2H18V4H20C21.1 4 22 4.9 22 6V20C22 21.1 21.1 22 20 22H4C2.9 22 2 21.1 2 20V6C2 4.9 2.9 4 4 4H6V2H8ZM4 8V20H20V8H4Z"
                        fill="#9B9FAA"
                    />
                </svg>
            </div>
        </div>
    );
};

export default DiaryDateCalendar;
