import { configureStore } from "@reduxjs/toolkit";
import { persistStore, persistReducer } from "redux-persist";
import storage from "redux-persist/lib/storage";
import { combineReducers } from "redux";
import userReducer from "./userSlice";
import loaderReducer from "./loaderSlice";
import modalReducer from "./modalSlice";
import caloriesReducer from "./caloriesSlice";

// âœ… Combine reducers
const rootReducer = combineReducers({
    user: userReducer,
    loader: loaderReducer,
    modal: modalReducer,
    calories: caloriesReducer,
});

// âœ… Persist configuration
const persistConfig = {
    key: "root",
    storage,
    whitelist: ["user", "calories"], // ðŸ”¥ AsigurÄƒ-te cÄƒ alimentele consumate sunt salvate
};

// âœ… Create persisted reducer
const persistedReducer = persistReducer(persistConfig, rootReducer);

// âœ… Configure store
const store = configureStore({
    reducer: persistedReducer,
    middleware: (getDefaultMiddleware) =>
        getDefaultMiddleware({
            serializableCheck: false,
        }),
});

// âœ… Persistor
const persistor = persistStore(store);

// ðŸ”¥ Debugging: Vezi dacÄƒ persistÄƒ corect
store.subscribe(() => {
    console.log("Updated store state:", store.getState());
});

export { store, persistor };
