import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    dailyCaloriesByDate: {},
    consumedFoods: {},
    selectedDate: new Date().toISOString().split("T")[0],
    forbiddenFoods: []  // ✅ Stocăm alimentele nerecomandate
};

const caloriesSlice = createSlice({
    name: "calories",
    initialState,
    reducers: {
        setDailyCalories: (state, action) => {
            const { date, calories } = action.payload;
            state.dailyCaloriesByDate[date] = calories;
        },
        setForbiddenFoods: (state, action) => {
            state.forbiddenFoods = action.payload;
        }
    }
});

export const { setDailyCalories, setForbiddenFoods } = caloriesSlice.actions;
export default caloriesSlice.reducer;
