import React from "react";
import { useSelector } from "react-redux";
import styles from "./Summary.module.css";

const Summary = ({ selectedDate }) => {
    const { dailyCaloriesByDate = {}, consumedFoods = {} } = useSelector((state) => state.calories);

    // ðŸ”¥ Ensure `selectedDate` is always a valid Date
    let dateObject = selectedDate instanceof Date && !isNaN(selectedDate) ? selectedDate : new Date();
    const formattedDate = dateObject.toISOString().split("T")[0];

    // âœ… Fetch the correct values based on selected date
    const dailyCalories = dailyCaloriesByDate[formattedDate] || 0;
    const consumedCalories = (consumedFoods[formattedDate] || []).reduce((total, food) => total + food.calories, 0);
    const remainingCalories = Math.max(dailyCalories - consumedCalories, 0);
    const percentageOfDailyIntake = dailyCalories > 0 ? ((consumedCalories / dailyCalories) * 100).toFixed(1) : 0;

    console.log("ðŸ“Œ [Summary] Selected Date:", formattedDate);
    console.log("ðŸ“Œ [Summary] Daily Calories:", dailyCalories);
    console.log("ðŸ“Œ [Summary] Consumed Calories:", consumedCalories);
    console.log("ðŸ“Œ [Summary] Remaining Calories:", remainingCalories);
    console.log("ðŸ“Œ [Summary] % of Normal:", percentageOfDailyIntake);

    return (
        <div className={styles.summaryContainer}>
            <h3 className={styles.summaryTitle}>Summary for {dateObject.toLocaleDateString("ro-RO")}</h3>
            <ul className={styles.summaryList}>
                <li><span>Calorii rÄƒmase</span> {remainingCalories.toFixed(2)} kcal</li>
                <li><span>Calorii consumate</span> {consumedCalories.toFixed(2)} kcal</li>
                <li><span>Calorii zilnice</span> {dailyCalories.toFixed(2)} kcal</li>
                <li><span>% din normÄƒ</span> {percentageOfDailyIntake}%</li>
            </ul>
        </div>
    );
};

export default Summary;
