import React, { useState } from "react";
import { useSelector } from "react-redux";
import DiaryDateCalendar from "../../components/DiaryDateCalendar/DiaryDateCalendar";
import DiaryAddProductForm from "../../components/DiaryAddProductForm/DiaryAddProductForm";
import DiaryProductsList from "../../components/DiaryProductsList/DiaryProductsList";
import Summary from "../../components/Summary/Summary";
import styles from "./DiaryPage.module.css";

const DiaryPage = () => {
    const [selectedDate, setSelectedDate] = useState(new Date());
    const dailyCalories = useSelector((state) => state.calories.dailyCaloriesByDate[selectedDate.toISOString().split("T")[0]]) || 0;

    return (
        <div className={styles.diaryContainer}>
            <div className={styles.leftContent}>
                <DiaryDateCalendar selectedDate={selectedDate} setSelectedDate={setSelectedDate} />
                <DiaryAddProductForm selectedDate={selectedDate} />
                <DiaryProductsList selectedDate={selectedDate} />
            </div>

            <div className={styles.rightContent}>
                <h3 className={styles.dailyCaloriesTitle}>Daily Calories Goal: {dailyCalories.toFixed(2)} kcal</h3>
                
                {/* ðŸ”¥ Trimitem `selectedDate` la `Summary.jsx` */}
                <Summary selectedDate={selectedDate} />
            </div>
        </div>
    );
};

export default DiaryPage;
