import React from "react";
import styles from "./DiaryProductsList.module.css";

const DiaryProductsList = ({ consumedFoods = [], onDeleteFood }) => {
    return (
        <div className={styles.productsListContainer}>
            <h3>Consumed Products</h3>
            {consumedFoods.length === 0 ? (
                <p>No products added yet.</p>
            ) : (
                <ul className={styles.productsList}>
                    {consumedFoods.map((food, index) => (
                        <li key={index}>
                            {/* ✅ Primul rând: alimentul la stânga, cantitatea și caloriile la dreapta */}
                            <div className={styles.productRow}>
                                <span className={styles.productName}>{food.name}</span>
                                <span className={styles.productDetails}>
                                    {food.weight}g - {food.calories.toFixed(2)} kcal
                                </span>
                            </div>

                            {/* ✅ Al doilea rând: butonul de ștergere */}
                            <button 
                                className={styles.deleteButton} 
                                onClick={() => onDeleteFood(index)}
                            >
                                ✕
                            </button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default DiaryProductsList;
