import React from "react";
import { useSelector } from "react-redux";
import styles from "./Summary.module.css";

const getFormattedDate = (selectedDate) => {
    if (!selectedDate) return "N/A";

    let dateObj = new Date(selectedDate);
    if (isNaN(dateObj)) {
        console.error("Invalid date:", selectedDate);
        return "N/A";
    }

    const day = String(dateObj.getDate()).padStart(2, "0");
    const month = String(dateObj.getMonth() + 1).padStart(2, "0");
    const year = dateObj.getFullYear();

    return `${day}.${month}.${year}`;
};

const Summary = ({ selectedDate }) => {
    const formattedDate = selectedDate 
        ? new Date(selectedDate).toISOString().split("T")[0] 
        : new Date().toISOString().split("T")[0];

    const dailyCalories = useSelector((state) => state.calories.dailyCalories);
    const consumedFoods = useSelector((state) => state.calories.consumedFoods[formattedDate] || []);
    
    const consumedCalories = consumedFoods.reduce((total, food) => total + food.calories, 0);
    const remainingCalories = dailyCalories - consumedCalories;
    const percentageOfDailyIntake = dailyCalories > 0 ? ((consumedCalories / dailyCalories) * 100).toFixed(1) : 0;

    return (
        <div className={styles.summaryContainer}>
            <h3 className={styles.summaryTitle}>Summary for {getFormattedDate(selectedDate)}</h3>
            <ul className={styles.summaryList}>
                <li><span>Left</span> {remainingCalories} kcal</li>
                <li><span>Consumed</span> {consumedCalories} kcal</li>
                <li><span>Daily Rate</span> {dailyCalories} kcal</li>
                <li><span>% of normal</span> {percentageOfDailyIntake}%</li>
            </ul>
        </div>
    );
};

export default Summary;
