import React from "react";
import { useSelector } from "react-redux";
import productsData from "../../data/products.json"; // ✅ Import products
import styles from "./Summary.module.css";

const getFormattedDate = () => {
    const today = new Date();
    return `${String(today.getDate()).padStart(2, "0")}.${String(today.getMonth() + 1).padStart(2, "0")}.${today.getFullYear()}`;
};

const Summary = () => {
    // ✅ Fetch only the necessary data from Redux
    const { dailyCalories = 0, consumedCalories = 0 } = useSelector((state) => state.calories || {});
    const bloodType = useSelector((state) => state.user?.bloodType); // ✅ Get user's blood type

    // ✅ Calculate remaining calories
    const remainingCalories = Math.max(dailyCalories - consumedCalories, 0);

    // ✅ Calculate percentage of daily intake
    const percentageOfDailyIntake = dailyCalories > 0 ? ((consumedCalories / dailyCalories) * 100).toFixed(1) : 0;

    // ✅ Get non-recommended foods based on blood type
    const nonRecommendedFoods = productsData.filter((product) => {
        if (bloodType === undefined || bloodType < 0 || bloodType > 4) return false;
        return product.groupBloodNotAllowed[bloodType]; // ✅ Check if food is not allowed for this blood type
    });

    return (
        <div className={styles.summaryContainer}>
            <h3 className={styles.summaryTitle}>Summary for {getFormattedDate()}</h3>
            <ul className={styles.summaryList}>
                <li><span>Left</span> {remainingCalories} kcal</li>
                <li><span>Consumed</span> {consumedCalories} kcal</li>
                <li><span>Daily Rate</span> {dailyCalories} kcal</li>
                <li><span>% of Normal</span> {percentageOfDailyIntake}%</li>
            </ul>

            {/* ✅ Display non-recommended foods based on blood type */}
            <div className={styles.nonRecommendedContainer}>
                <h4>⚠ Not Recommended for Your Blood Type</h4>
                {nonRecommendedFoods.length === 0 ? (
                    <p>All foods are allowed.</p>
                ) : (
                    <ul className={styles.nonRecommendedList}>
                        {nonRecommendedFoods.map((food) => (
                            <li key={food._id.$oid}>{food.title} - {food.calories} kcal</li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
};

export default Summary;
