import React from "react";
import { useDispatch } from "react-redux";
import { removeConsumedFood } from "../../redux/caloriesSlice"; // ✅ Asigură-te că este importat corect
import styles from "./DiaryProductsList.module.css";

const DiaryProductsList = ({ consumedFoods = [], selectedDate }) => {
    const dispatch = useDispatch();

    const handleRemove = (index) => {
        const formattedDate = selectedDate instanceof Date
            ? selectedDate.toISOString().split("T")[0]
            : new Date(selectedDate).toISOString().split("T")[0];

        dispatch(removeConsumedFood({ date: formattedDate, index }));
    };

    return (
        <div className={styles.productsListContainer}>
            {consumedFoods.length === 0 ? (
                <p>No products added yet.</p>
            ) : (
                <ul className={styles.productsList}>
                    {consumedFoods.map((food, index) => (
                        <li key={index}>
                            <span className={styles.productName}>{food.name}</span>
                            <span className={styles.productWeight}>{food.weight}g</span>
                            <span className={styles.productCalories}>{food.calories.toFixed(2)} kcal</span>
                            <button className={styles.deleteButton} onClick={() => handleRemove(index)}>✕</button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default DiaryProductsList;
