import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import productsData from "../../data/products.json"; // âœ… ImportÄƒm produsele
import styles from "./Summary.module.css";

const Summary = ({ selectedDate }) => {
    const navigate = useNavigate();
    const { dailyCaloriesByDate = {}, consumedFoods = {} } = useSelector((state) => state.calories);
    const { user } = useSelector((state) => state.user); // âœ… PreluÄƒm grupa de sÃ¢nge a utilizatorului

    const [forbiddenFoods, setForbiddenFoods] = useState([]); // âœ… StocÄƒm produsele interzise local
    const [calories, setCalories] = useState(null);

    let dateObject = selectedDate instanceof Date && !isNaN(selectedDate) ? selectedDate : new Date();
    const formattedDate = dateObject.toISOString().split("T")[0];

    useEffect(() => {
        console.log("ðŸ“Œ [Summary] Selected Date:", formattedDate);
        console.log("ðŸ“Œ [Summary] Daily Calories:", dailyCaloriesByDate[formattedDate] || 0);
        console.log("ðŸ“Œ [Summary] Consumed Calories:", (consumedFoods[formattedDate] || []).reduce((total, food) => total + food.calories, 0));
        
        console.log("ðŸ”Ž [Redux] User Data:", user);
        console.log("ðŸ“¦ [Fetched Products]", productsData);
        
        if (user?.bloodType) {
            const bloodIndex = parseInt(user.bloodType, 10);
            console.log("ðŸ©¸ Blood Type:", bloodIndex);
    
            if (!isNaN(bloodIndex) && bloodIndex >= 1 && bloodIndex <= 4) {
                const filteredProducts = productsData.filter(product => product.groupBloodNotAllowed[bloodIndex] === true);
                console.log("ðŸš« Forbidden Foods Calculated:", filteredProducts);
                setForbiddenFoods(filteredProducts);
            } else {
                console.warn("âš ï¸ Invalid blood type:", user.bloodType);
            }
        } else {
            console.warn("âš ï¸ User blood type is missing!");
        }
        
        setCalories(dailyCaloriesByDate[formattedDate] || 0);
    }, [formattedDate, dailyCaloriesByDate, consumedFoods, user]);
    
    const dailyCalories = dailyCaloriesByDate[formattedDate] || 0;
    const consumedCalories = (consumedFoods[formattedDate] || []).reduce((total, food) => total + food.calories, 0);
    const remainingCalories = Math.max(dailyCalories - consumedCalories, 0);
    const percentageOfDailyIntake = dailyCalories > 0 ? ((consumedCalories / dailyCalories) * 100).toFixed(1) : 0;

    const handleLogout = () => {
        localStorage.removeItem("token");
        navigate("/login");
    };

    return (
        <div className={styles.summaryContainer}>
            <div className={styles.summaryHeader}>
                <button className={styles.navButton} onClick={() => navigate("/summary")}>Nic</button>
                <div className={styles.verticalBar}></div>
                <button className={styles.logoutButton} onClick={handleLogout}>Exit</button>
            </div>

            <h3 className={styles.summaryTitle}>Summary for {dateObject.toLocaleDateString("ro-RO")}</h3>
            
            <ul className={styles.summaryList}>
                <li><span>Left</span> {remainingCalories.toFixed(2)} kcal</li>
                <li><span>Consumed</span> {consumedCalories.toFixed(2)} kcal</li>
                <li><span>Daily rate</span> {dailyCalories.toFixed(2)} kcal</li>
                <li><span>% of normal</span> {percentageOfDailyIntake}%</li>
            </ul>

            {/* ðŸ”¹ Lista produselor nerecomandate */}
            <div className={styles.forbiddenFoodsContainer}>
                <h2>Your recommended daily calorie intake is</h2>
                <p className={styles.caloriesNumber}>{calories} kcal</p>
                <hr className={styles.modalDivider} />
                <h3 className={styles.title2}>Foods you should avoid:</h3>
                <ol className={styles.forbiddenList}>
                    {forbiddenFoods.length > 0 ? (
                        forbiddenFoods.map((food, index) => (
                            <li key={food._id}>{index + 1}. {food.title}</li>
                        ))
                    ) : (
                        <li>No restricted foods found.</li>
                    )}
                </ol>
            </div>
        </div>
    );
};

export default Summary;
