import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    dailyCalories: 0,
    consumedCalories: 0,
    consumedFoods: [], // ✅ Added for tracking consumed foods
};

const caloriesSlice = createSlice({
    name: "calories",
    initialState,
    reducers: {
        // ✅ Sets daily calorie intake
        setDailyCalories: (state, action) => {
            state.dailyCalories = action.payload;
        },

        // ✅ Adds calories consumed
        addConsumedCalories: (state, action) => {
            state.consumedCalories += action.payload;
        },

        // ✅ Adds a consumed food item
        addConsumedFood: (state, action) => {
            state.consumedFoods.push(action.payload);
            state.consumedCalories += action.payload.calories;
        },

        // ✅ Removes a consumed food item
        removeConsumedFood: (state, action) => {
            const index = action.payload;
            if (index >= 0 && index < state.consumedFoods.length) {
                state.consumedCalories -= state.consumedFoods[index].calories;
                state.consumedFoods.splice(index, 1);
            }
        },
    },
});

// ✅ Export actions
export const { setDailyCalories, addConsumedCalories, addConsumedFood, removeConsumedFood } = caloriesSlice.actions;

// ✅ Export reducer
export default caloriesSlice.reducer;
