import React from "react";
import styles from "./DiaryProductsList.module.css";

useEffect(() => {
    const checkScroll = () => {
        if (listRef.current) {
            const { scrollHeight, clientHeight, scrollTop } = listRef.current;
            setIsScrollable(scrollHeight > clientHeight && scrollTop < scrollHeight - clientHeight);
        }
    };

    checkScroll(); // ✅ Check on mount
    listRef.current?.addEventListener("scroll", checkScroll); // ✅ Listen to scroll events

    return () => listRef.current?.removeEventListener("scroll", checkScroll); // ✅ Cleanup on unmount
}, [consumedFoods]);

const DiaryProductsList = ({ consumedFoods = [], onDeleteFood }) => {
    return (
        <div className={styles.productsListContainer}>
            {consumedFoods.length === 0 ? (
                <p>No products added yet.</p>
            ) : (
                <div className={`${styles.scrollableContainer} ${isScrollable ? styles.hasShadow : ""}`} ref={listRef}>
                <ul className={styles.productsList}>
                    {consumedFoods.map((food, index) => (
                        <li key={index}>
                            {/* ✅ Aliment (stânga) */}
                            <span className={styles.productName}>{food.name}</span>

                            {/* ✅ Cantitate (dreapta) */}
                            <span className={styles.productWeight}>{food.weight}g</span>

                            {/* ✅ Calorii (dreapta) */}
                            <span className={styles.productCalories}>
                                {food.calories.toFixed(2)} kcal
                            </span>

                            {/* ✅ Butonul de ștergere (X) */}
                            <button 
                                className={styles.deleteButton} 
                                onClick={() => onDeleteFood(index)} // ✅ Acum funcția este definită corect
                            >
                                ✕
                            </button>
                        </li>
                    ))}
                </ul>
                </div>
            )}
        </div>
    );
};

export default DiaryProductsList;
