import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { showLoader, hideLoader, openModal } from "../../redux/"; // ✅ Eliminat `closeModal`
import Logo from "../../components/Logo/Logo";
import Loader from "../../components/Loader/Loader";
import Modal from "../../components/Modal/Modal";
import styles from "./HomePage.module.css"; 
import productsData from "../../data/products.json"; 

const HomePage = () => {
    const dispatch = useDispatch();
    const isLoading = useSelector((state) => state.loader?.isLoading || false);
    const isModalOpen = useSelector((state) => state.modal?.isOpen || false);

    const [height, setHeight] = useState("");
    const [age, setAge] = useState("");
    const [currentWeight, setCurrentWeight] = useState("");
    const [desiredWeight, setDesiredWeight] = useState("");
    const [bloodType, setBloodType] = useState("");
    const [calories, setCalories] = useState(null);
    const [forbiddenFoods, setForbiddenFoods] = useState([]); // ✅ Acum este utilizat în cod

    const calculateCalories = (e) => {
        e.preventDefault();
        if (!height || !age || !currentWeight || !desiredWeight || !bloodType) {
            alert("Please fill in all fields.");
            return;
        }

        dispatch(showLoader());

        setTimeout(() => {
            const dailyCalories = (10 * currentWeight) + (6.25 * height) - (5 * age) - 161 - (10 * (currentWeight - desiredWeight));
            setCalories(Math.round(dailyCalories));

            const bloodIndex = parseInt(bloodType, 10);
            if (bloodIndex >= 1 && bloodIndex <= 4) {
                const filteredProducts = productsData.filter(product => product.groupBloodNotAllowed[bloodIndex] === true);
                setForbiddenFoods(filteredProducts);
            }

            dispatch(hideLoader());
            dispatch(openModal());
        }, 1000);
    };

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-white p-6">
            {isLoading && <Loader />}
            
            <header className={styles.header}>
                <Logo />
                <nav className={styles.nav}>
                    <a href="/login">Log in</a>
                    <a href="/register">Registration</a>
                </nav>
            </header>

            <form className={styles.formContainer} onSubmit={calculateCalories}>
                <input type="number" placeholder="Height (cm) *" value={height} onChange={(e) => setHeight(e.target.value)} />
                <button type="submit">Calculate</button>
            </form>

            {isModalOpen && (
                <Modal>
                    <p>{calories} kcal</p>
                    <ul>
                        {forbiddenFoods.map((food, index) => (
                            <li key={index}>{food.title}</li>
                        ))}
                    </ul>
                </Modal>
            )}
        </div>
    );
};

export default HomePage;
