import React from "react";
import styles from "./DiaryDateCalendar.module.css";
import CalendarIcon from "../../assets/calendar 1.svg"; // ✅ Import the custom SVG

const DiaryDateCalendar = ({ selectedDate, setSelectedDate }) => {
    // ✅ Ensure selectedDate is always a Date object
    const validDate = selectedDate instanceof Date && !isNaN(selectedDate) ? selectedDate : new Date();

    // ✅ Format the date to `dd.mm.yyyy`
    const formattedDate = validDate.toLocaleDateString("ro-RO", {
        day: "2-digit",
        month: "2-digit",
        year: "numeric",
    });

    return (
        <div className={styles.calendarContainer}>
            {/* ✅ Wrapper for input and icon */}
            <div className={styles.datePickerWrapper}>
                {/* ✅ Hidden Date Input */}
                <input
                    type="date"
                    id="dateInput"
                    value={validDate.toISOString().split("T")[0]}
                    onChange={(e) => setSelectedDate(new Date(e.target.value))}
                    className={styles.hiddenDateInput}
                />

                {/* ✅ Displayed date text */}
                <span className={styles.dateText} onClick={() => document.getElementById("dateInput").click()}>
                    {formattedDate}
                </span>

                {/* ✅ Custom SVG icon */}
                <img
                    src={CalendarIcon}
                    alt="Calendar Icon"
                    className={styles.calendarIcon}
                    onClick={() => document.getElementById("dateInput").click()} // ✅ Trigger input on click
                />
            </div>
        </div>
    );
};

export default DiaryDateCalendar;
