import React, { useState } from "react";
import DiaryDateCalendar from "../../components/DiaryDateCalendar/DiaryDateCalendar";
import DiaryAddProductForm from "../../components/DiaryAddProductForm/DiaryAddProductForm";
import DiaryProductsList from "../../components/DiaryProductsList/DiaryProductsList";
import RightSidebar from "../../components/RightSideBar/RightSideBar";
import styles from "./DiaryPage.module.css";

const DiaryPage = () => {
    const [selectedDate, setSelectedDate] = useState(new Date());
    const [consumedFoods, setConsumedFoods] = useState([]);

    const handleAddFood = (food) => {
        setConsumedFoods([...consumedFoods, food]);
    };

    return (
        <div className={styles.diaryContainer}>
           
            <div className={styles.formContainer}>
                {/* ✅ Coloana din stânga cu Calendar și Formular */}
                <div className={styles.leftColumn}>
                    <DiaryDateCalendar selectedDate={selectedDate} setSelectedDate={setSelectedDate} />
                    <DiaryAddProductForm selectedDate={selectedDate} onAddFood={handleAddFood} />
                </div>

                {/* ✅ Coloana din dreapta cu Lista de Produse */}
                <div className={styles.rightColumn}>
                    <DiaryProductsList consumedFoods={consumedFoods} />
                </div>
            </div>

            {/* ✅ Sidebar menținut */}
            <RightSidebar />
        </div>
    );
};

export default DiaryPage;
