import React, { useState } from "react";
import DiaryDateCalendar from "../../components/DiaryDateCalendar/DiaryDateCalendar";
import DiaryAddProductForm from "../../components/DiaryAddProductForm/DiaryAddProductForm";
import DiaryProductsList from "../../components/DiaryProductsList/DiaryProductsList";
import { useDispatch, useSelector } from "react-redux";
import { addConsumedFood, removeConsumedFood } from "../../redux/caloriesSlice";
import styles from "./DiaryPage.module.css";

const DiaryPage = () => {
    const dispatch = useDispatch();
    
    // ✅ Ensure `selectedDate` is initialized as a `Date` object
    const [selectedDate, setSelectedDate] = useState(new Date());

    const consumedFoods = useSelector((state) => state.calories.consumedFoods);

    const handleAddFood = (food) => {
        dispatch(addConsumedFood(food));
    };

    const handleRemoveFood = (index) => {
        dispatch(removeConsumedFood(index));
    };

    return (
        <div className={styles.diaryContainer}>
            <div className={styles.leftContent}>
                {/* ✅ Pass a valid `selectedDate` */}
                <DiaryDateCalendar selectedDate={selectedDate} setSelectedDate={setSelectedDate} />
                <DiaryAddProductForm selectedDate={selectedDate} onAddFood={handleAddFood} />
                <DiaryProductsList consumedFoods={consumedFoods} onDeleteFood={handleRemoveFood} />
            </div>
        </div>
    );
};

export default DiaryPage;
