import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    dailyCaloriesByDate: {}, // ðŸ”¥ StocheazÄƒ caloriile per zi { "YYYY-MM-DD": 1800 }
    consumedFoods: {}, // ðŸ”¥ StocheazÄƒ alimentele consumate per zi { "YYYY-MM-DD": [{ aliment }] }
    selectedDate: new Date().toISOString().split("T")[0], // ðŸ”¥ Ne asigurÄƒm cÄƒ data implicitÄƒ este validÄƒ
    forbiddenFoods: [] // âœ… AdÄƒugÄƒm un array pentru a stoca produsele nerecomandate
};

const caloriesSlice = createSlice({
    name: "calories",
    initialState,
    reducers: {
        setDailyCalories: (state, action) => {
            const { date, calories, forbiddenFoods } = action.payload;
            state.dailyCaloriesByDate[date] = calories;
            state.forbiddenFoods = forbiddenFoods || []; // âœ… SalvÄƒm alimentele nerecomandate
        },

        setSelectedDate: (state, action) => {
            const newDate = new Date(action.payload);
            if (!isNaN(newDate.getTime())) {
                state.selectedDate = newDate.toISOString().split("T")[0];
            } else {
                console.error("ðŸš¨ Invalid date received in Redux. Resetting to today.");
                state.selectedDate = new Date().toISOString().split("T")[0];
            }
        },

        addConsumedFood: (state, action) => {
            const { date, name, weight, calories } = action.payload;
            if (!state.consumedFoods[date]) {
                state.consumedFoods[date] = [];
            }
            state.consumedFoods[date].push({ name, weight, calories });
        },

        removeConsumedFood: (state, action) => {
            const { date, index } = action.payload;
            if (state.consumedFoods[date]) {
                state.consumedFoods[date].splice(index, 1);
                if (state.consumedFoods[date].length === 0) {
                    delete state.consumedFoods[date];
                }
            }
        },
    },
});

export const { setDailyCalories, setSelectedDate, addConsumedFood, removeConsumedFood } = caloriesSlice.actions;
export default caloriesSlice.reducer;
