import React, { useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import Logo from "../../components/Logo/Logo";
import styles from "./HomePage.module.css"; 
import productsData from "../../data/products.json"; // ✅ Importăm lista de produse

const HomePage = () => {
    const location = useLocation();
    const navigate = useNavigate();

    const [height, setHeight] = useState("");
    const [age, setAge] = useState("");
    const [currentWeight, setCurrentWeight] = useState("");
    const [desiredWeight, setDesiredWeight] = useState("");
    const [bloodType, setBloodType] = useState("");

    const [calories, setCalories] = useState(null);
    const [forbiddenFoods, setForbiddenFoods] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);

    const calculateCalories = (e) => {
        e.preventDefault();
        if (!height || !age || !currentWeight || !desiredWeight || !bloodType) {
            alert("Please fill in all fields.");
            return;
        }
    
        const dailyCalories = (10 * currentWeight) + (6.25 * height) - (5 * age) - 161 - (10 * (currentWeight - desiredWeight));
        setCalories(Math.round(dailyCalories));
    
        // ✅ Resetăm lista înainte de încărcare
        setForbiddenFoods([]);
    
        // ✅ Corectăm indexul pentru grupele de sânge (0 -> 0, 1 -> 1, 2 -> 2, 3 -> 3)
        const bloodIndex = parseInt(bloodType, 10) ; 
        if (bloodIndex < 0 || bloodIndex > 3) return; // ✅ Asigurăm că avem un index valid
    
        // ✅ Filtrăm produsele și eliminăm cele cu `null` la grupa respectivă
        const filteredProducts = productsData.filter(product => 
            product.groupBloodNotAllowed[bloodIndex] !== null && product.groupBloodNotAllowed[bloodIndex] === true
        );
    
        setForbiddenFoods(filteredProducts);
        setIsModalOpen(true);
    };

    const handleStartLosingWeight = () => {
        setIsModalOpen(false); // ✅ Închidem modalul
        setTimeout(() => {
            navigate("/login"); // ✅ Navigăm la Login
        }, 300); // ✅ Mică întârziere pentru efect vizual
    };

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-white p-6">
        
            {/* ✅ Header */}
            <header className={styles.header}>
                <div className={styles.logo}>
                    <Logo />
                </div>
                <div className={styles.verticalBar}></div>
                <nav className={styles.nav}>
                    <Link to="/login" className={styles.navLink}>Log in</Link>
                    <Link to="/register" className={styles.navLink}>Registration</Link>
                </nav>
            </header>

            {!(location.pathname === "/login" || location.pathname === "/register") && (
                <>
                    <h1 className={styles.title}>
                        Calculate your daily calorie intake right now
                    </h1>

                    {/* ✅ Formular */}
                    <form className={styles.formContainer} onSubmit={calculateCalories}>
                        <div className={styles.leftColumn}>
                            <input 
                                type="number" 
                                placeholder="Height (cm) *"
                                value={height} 
                                onChange={(e) => setHeight(e.target.value)}
                                className={styles.inputField} 
                            />

                            <input 
                                type="number" 
                                placeholder="Age *" 
                                value={age} 
                                onChange={(e) => setAge(e.target.value)}
                                className={styles.inputField} 
                            />

                            <input 
                                type="number" 
                                placeholder="Current weight *" 
                                value={currentWeight} 
                                onChange={(e) => setCurrentWeight(e.target.value)}
                                className={styles.inputField} 
                            />
                        </div>

                        <div className={styles.rightColumn}>
                            <input 
                                type="number" 
                                placeholder="Desired weight *" 
                                value={desiredWeight} 
                                onChange={(e) => setDesiredWeight(e.target.value)}
                                className={styles.inputField} 
                            />

                            {/* ✅ Blood Type */}
                            <div className={styles.bloodTypeContainer}>
                                <label className={styles.bloodTypeLabel}>Blood Type*</label>
                                <ul className={styles.bloodTypeOptions}>
                                    {[1, 2, 3, 4].map((type) => (
                                        <li key={type}>
                                            <input 
                                                type="radio" 
                                                name="bloodType" 
                                                value={type} 
                                                checked={bloodType === String(type)} 
                                                onChange={(e) => setBloodType(e.target.value)} 
                                            />
                                            {type}
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        </div>

                        <button type="submit" className={styles.submitButton}>Start losing weight</button>
                    </form>
                </>
            )}

            {/* ✅ Modal pentru caloriile recomandate */}
            {isModalOpen && (
                <div className={styles.modalOverlay}>
                    <div className={`${styles.modalContent} ${isModalOpen ? styles.modalOpen : styles.modalClose}`}>
                        {/* ✅ Buton de închidere */}
                        <button className={styles.closeButton} onClick={() => setIsModalOpen(false)}>×</button>

                        <h2>Your recommended daily calorie intake is</h2>
                        <p className={styles.caloriesNumber}>{calories} kcal</p>
                        <hr className={styles.modalDivider} />

                        <h3 className={styles.title2}>Foods you should avoid:</h3>
                        <ol className={styles.forbiddenList}>
                            {forbiddenFoods.length > 0 ? (
                                forbiddenFoods.map((food, index) => (
                                    <li key={food._id.$oid}>{index + 1}. {food.title}</li>
                                ))
                            ) : (
                                <li>No restricted foods found.</li>
                            )}
                        </ol>

                        <button className={styles.startButtonModal} onClick={handleStartLosingWeight}>
                            Start losing weight
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default HomePage;
