import { useSelector, useDispatch } from "react-redux";
import { closeModal } from "../../redux/modalSlice";
import { useEffect } from "react";

const Modal = () => {
    const dispatch = useDispatch();
    const { isOpen, content } = useSelector((state) => state.modal);

    useEffect(() => {
        const handleKeyDown = (event) => {
            if (event.key === "Escape") {
                dispatch(closeModal());
            }
        };
        if (isOpen) {
            window.addEventListener("keydown", handleKeyDown);
        }
        return () => {
            window.removeEventListener("keydown", handleKeyDown);
        };
    }, [isOpen, dispatch]);

    if (!isOpen) return null;

    return (
        <div
            className="fixed top-0 left-0 w-full h-full flex items-center justify-center bg-opacity-50 bg-gray-500"
            onClick={() => dispatch(closeModal())}
        >
            <div className="bg-white p-4 rounded-lg" onClick={(e) => e.stopPropagation()}>
                {content}
                <button onClick={() => dispatch(closeModal())}>Close</button>
            </div>
        </div>
    );
};

export default Modal;
