import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { removeConsumedFood } from "../../redux/caloriesSlice"; // ✅ Import Redux action

const DiaryProductsList = ({ selectedDate }) => {
    const dispatch = useDispatch();
    const formattedDate = new Date(selectedDate).toISOString().split("T")[0];
    const consumedFoods = useSelector((state) => state.calories.consumedFoods[formattedDate] || []);

    const handleRemoveFood = (index) => {
        dispatch(removeConsumedFood({ date: formattedDate, index }));
    };

    return (
        <div>
            <h3>Consumed Foods for {formattedDate}</h3>
            <ul>
                {consumedFoods.map((food, index) => (
                    <li key={index}>
                        {food.name} - {food.calories} kcal
                        <button onClick={() => handleRemoveFood(index)}>Remove</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default DiaryProductsList;
