import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { addConsumedCalories } from "../../redux/caloriesSlice";
import styles from "./DiaryPage.module.css";

const DiaryPage = () => {
    const dispatch = useDispatch();
    const [date, setDate] = useState(new Date().toISOString().split("T")[0]);
    const [product, setProduct] = useState("");
    const [weight, setWeight] = useState("");
    const [consumedFoods, setConsumedFoods] = useState([]);
    const caloriesPerGram = 1.5; // Exemplu, poate fi diferit

    const handleAddFood = () => {
        if (!product || !weight) return;
    
        const calories = weight * caloriesPerGram;
        setConsumedFoods([...consumedFoods, { product, weight, calories }]);
        dispatch(addConsumedCalories(calories));  // ✅ Adăugăm caloriile în Redux
        setProduct("");
        setWeight("");
    };
    

    const handleDeleteFood = (index) => {
        const newFoods = [...consumedFoods];
        const removedFood = newFoods.splice(index, 1)[0];
        setConsumedFoods(newFoods);
        dispatch(addConsumedCalories(-removedFood.calories));  // ✅ Scădem caloriile
    };
    

    return (
        <div className={styles.diaryContainer}>
            <h2>{date}</h2>
            <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />

            <div className={styles.foodInput}>
                <input 
                    type="text" 
                    placeholder="Product Name"
                    value={product} 
                    onChange={(e) => setProduct(e.target.value)} 
                />
                <input 
                    type="number" 
                    placeholder="Weight (g)"
                    value={weight} 
                    onChange={(e) => setWeight(e.target.value)} 
                />
                <button onClick={handleAddFood}>+</button>
            </div>

            <ul className={styles.foodList}>
                {consumedFoods.map((food, index) => (
                    <li key={index}>
                        {food.product} - {food.weight}g - {food.calories} kcal
                        <button onClick={() => handleDeleteFood(index)}>X</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default DiaryPage;
