import React, { useState, useEffect } from "react";
import productsData from "../../data/products.json"; // ✅ Importăm lista de produse
import styles from "./DiaryAddProductForm.module.css";

const DiaryAddProductForm = ({ onAddFood }) => {
    const [product, setProduct] = useState("");
    const [weight, setWeight] = useState("");
    const [calories, setCalories] = useState(0);
    const [filteredProducts, setFilteredProducts] = useState([]);

    useEffect(() => {
        if (product.length > 0) {
            const filtered = productsData.filter((p) =>
                p.title.toLowerCase().includes(product.toLowerCase())
            );
            setFilteredProducts(filtered);
        } else {
            setFilteredProducts([]);
        }
    }, [product]);

    const handleSelectProduct = (selectedProduct) => {
        setProduct(selectedProduct.title);
        setCalories(selectedProduct.calories);
        setFilteredProducts([]); // ✅ Hide suggestions after selection
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!product || !weight) return;

        const totalCalories = (weight / 100) * calories; // ✅ Correct calorie calculation

        onAddFood({ name: product, weight, calories: totalCalories });
        setProduct("");
        setWeight("");
        setCalories(0);
    };

    return (
        <div className={styles.formContainer}>
            <form onSubmit={handleSubmit} className={styles.formGroup}>
                {/* ✅ Food Name Input with Suggestions */}
                <div className={styles.inputWrapper}>
                    <input
                        type="text"
                        placeholder="Enter food name"
                        value={product}
                        onChange={(e) => setProduct(e.target.value)}
                        className={styles.inputField}
                    />
                    {/* ✅ Dropdown for food suggestions */}
                    {filteredProducts.length > 0 && (
                        <ul className={styles.suggestions}>
                            {filteredProducts.slice(0, 5).map((p) => (
                                <li key={p._id.$oid} onClick={() => handleSelectProduct(p)}>
                                    {p.title} ({p.calories} kcal/100g)
                                </li>
                            ))}
                        </ul>
                    )}
                </div>

                {/* ✅ Weight Input */}
                <input
                    type="number"
                    placeholder="Grams"
                    value={weight}
                    onChange={(e) => setWeight(e.target.value)}
                    className={styles.inputField}
                />

                {/* ✅ Submit Button */}
                <button type="submit" className={styles.submitButton}>+</button>
            </form>
        </div>
    );
};

export default DiaryAddProductForm;
